    public void Analyze() {
        int [,] newMap = Transpose(map); //swapping rows and columns because I was a bit dumb
        new TraversabilityAnalyzer(newMap, newMap.GetLength(0), newMap.GetLength(1), TraversabilityAnalyzer.PCGType.MARIO_1_1);
    }

    public static int[,] Transpose(int[,] matrix) {
        int w = matrix.GetLength(0);
        int h = matrix.GetLength(1);

        int[,] transposedMatrix = new int[h,w];

        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                transposedMatrix[i,j] = matrix[w-j-1,i];
            }
        }

        return transposedMatrix;
    }

    public static int[,] FlipY(int[,] matrix) {
        int w = matrix.GetLength(0);
        int h = matrix.GetLength(1);

        int[,] transposedMatrix = new int[w, h];

        for (int i = 0; i < w; i++) {
            for (int j = 0; j < h; j++) {
                transposedMatrix[i, j] = matrix[i, h-j-1];
            }
        }

        return transposedMatrix;
    }
